import React, { useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import { AuthContext } from '../'; // –ø—Ä–æ–≤–µ—Ä—å –ø—É—Ç—å

const Logout = () => {
  const { logout } = useContext(AuthContext); // üí• –∏—Å–ø–æ–ª—å–∑—É–µ–º useContext
  const navigate = useNavigate();

  const handleLogout = async () => {
    try {
      await logout(); // –≤—ã–∑—ã–≤–∞–µ–º logout –∏–∑ –∫–æ–Ω—Ç–µ–∫—Å—Ç–∞
      navigate('/login'); // —Ä–µ–¥–∏—Ä–µ–∫—Ç –ø–æ—Å–ª–µ –≤—ã—Ö–æ–¥–∞
    } catch (e) {
      console.error("–û—à–∏–±–∫–∞ –ø—Ä–∏ –≤—ã—Ö–æ–¥–µ:", e);
    }
  };

  return (
    <button onClick={handleLogout} className="logout-btn">
      Logout
    </button>
  );
};

export default Logout;
