import './App.css';
import { User } from './getUser/User';
import { AddUser } from './adduser/AddUser';
import Home from './Components/HomePage/Home';
import Header from './Components/Header/Header';
import { createBrowserRouter, RouterProvider, Outlet } from "react-router-dom";

// 1. Создаем макет (layout) с Header и Outlet (место для контента)
const RootLayout = () => {
  return (
    <div className="App">
      <Header />
      <Outlet /> {/* Здесь будут отображаться компоненты роутов */}
    </div>
  );
};

function App() {
  const route = createBrowserRouter([
    {
      path: "/",
      element: <RootLayout />, // Используем макет для всех дочерних роутов
      children: [
        {
          path: "/users",
          element: <User />
        },
        {
          path: "/add",
          element: <AddUser />
        },
        {
          index: true, // Главная страница
          element: <Home />
        }
      ]
    }
  ]);

  return <RouterProvider router={route} />;
}

export default App;
